angular.module("myApp",["ngRoute"]).config(["$routeProvider",function(e){e.when("/",{templateUrl:"home.html",controller:"myController"}).when("/new-meal",{templateUrl:"meal.html",controller:"myController"}).when("/earnings",{templateUrl:"earnings.html",controller:"myController"}).otherwise("/")}]).controller("myController",["$scope",function(e){e.meal={},e.customer={},e.earnings={},e.submit=function(){e.myForm.$valid&&(e.customer.subtotal=e.meal.price+e.meal.price*(e.meal.tax/100),e.customer.tip=e.customer.subtotal*(e.meal.tip/100),e.customer.total=e.customer.subtotal+e.customer.tip,e.earnings.total=e.customer.tip+(e.earnings.total||0),e.earnings.count=++e.earnings.count||1,e.earnings.avg=e.earnings.total/e.earnings.count)},e.clear=function(){e.meal={},e.customer={},e.myForm.$setPristine()},e.resetAll=function(){e.myForm.$setPristine(),e.meal={},e.customer={},e.earnings={}}}]);